<script src="<?php echo APP_ASSETS ?>libs/owlCarousel2/owlCarousel.min.js"></script>
<script>
var owl = $(".header__carousel .wrap");
owl.addClass('owl-carousel').owlCarousel({
  autoplay: 1,
  autoplayTimeout: 5000,
  animateIn: 'fadeIn',
  animateOut: 'fadeOut',
  items: 1,
  dots: 0,
  loop: 1,
  infinite: 1,
  onChange: function(e){
    if(e.item.index != 0) {
      $(".header__owlnav .textNav .index").html('0' + (e.item.index))
      $(".header__owlnav .textNav .total").html('0' + e.item.count)
    }
  },
  onInitialized: function(e) {}
})
$(".owl-next").click(function(){
  owl.trigger('next.owl.carousel')
})
$(".owl-prev").click(function(){
  owl.trigger('prev.owl.carousel')
})


$(".groupBox__wrap").addClass('hide')
$(".groupBox__wrap").on('inview', function(event, isInView) {
  if(isInView) $(".groupBox__wrap").addClass('animation');
  else $(".groupBox__wrap").removeClass('animation');
});
$(".section--half__title").on('inview', function(event, isInView) {
  if(isInView) {
    $(this).addClass('fadeInUp');
    $(this).next().addClass('fadeInUp');
  } else {
    $(this).removeClass('fadeInUp');
    $(this).next().removeClass('fadeInUp');
  }
});

$(".section06__img01").on('inview', function(event, isInView) {
  if(isInView) {
    $(this).addClass('fadeInUp');
    $(this).next().addClass('fadeInUp');
    $(this).next().next().addClass('fadeInUp');
  } else {
    $(this).removeClass('fadeInUp');
    $(this).next().removeClass('fadeInUp');
    $(this).next().next().removeClass('fadeInUp');
  }
});
</script>
